var buyLink=document.querySelectorAll(".button-buy"),messagePopup=document.querySelector(".modal-cart-message"),messageClose=messagePopup.querySelector(".modal-close");buyLink.forEach(function(e){e.addEventListener("click",function(e){e.preventDefault(),messagePopup.classList.add("modal-show")})}),messageClose.addEventListener("click",function(e){e.preventDefault(),messagePopup.classList.remove("modal-show")}),window.addEventListener("keydown",function(e){27===e.keyCode&&(e.preventDefault(),messagePopup.classList.contains("modal-show")&&messagePopup.classList.remove("modal-show"))});
